class Character {
public:
    // Position is matrix[y][x] where matrix[0][0] is the top left corner of the field
    int x;
    int y;
    char skin;
    Style style;

    Character(int x, int y, char skin, Style style) {
        this->x = x;
        this->y = y;
        this->skin = skin;
        this->style = style;
    }
};